#include "poll_client.h"
#include <unistd.h>
#include <assert.h>
#include <atomic>

std::atomic<int> g_req_cnt(0);

void disconn_event()
{
    std::cout << "server quit...!!!" << std::endl;
}

int64_t diff_us(struct timespec& start, struct timespec& end)
{
    return (end.tv_sec - start.tv_sec) * 1000000 + ((end.tv_nsec - start.tv_nsec) / 1000); 
}

void do_respone(char* resp_buff, uint64_t size)
{
    g_req_cnt++;
    assert(size == 1024);
}

int main()
{
    signal(SIGPIPE, SIG_IGN);
    // 1KB, 1000000 reqs, 9549510 us
    std::string str_1K = "";
    try
    {
        g_req_cnt = 0;
        struct timespec begin, end; 
        UDSockClient client;
        if (!client.Init(kServerAddress, &disconn_event))
        {
            perror("Init");
            return -1;
        }
        int max_cnt = 1000000;
        clock_gettime(CLOCK_REALTIME, &begin);
        for (int i = 0; i < max_cnt; i++)
        {
            client.SendRequest(str_1K, do_respone);
        }
        clock_gettime(CLOCK_REALTIME, &end);
        while(g_req_cnt.load() < max_cnt)
            usleep(1000);
        std::cout << "spend: " << diff_us(begin, end) << " us" << std::endl;
        client.Stop();
    }
    catch(const std::exception& e)
    {
        std::cerr << e.what() << '\n';
    }
    
    return 0;
}